SELECT "URLHash", date_trunc('day', from_unixtime("EventTime")), COUNT(*) AS PageViews FROM hits WHERE "CounterID" = 62 AND date_trunc('day', from_unixtime("EventTime")) >= '2013-07-01' AND date_trunc('day', from_unixtime("EventTime")) <= '2013-07-31' AND "IsRefresh" = 0 AND "TraficSourceID" IN (-1, 6) AND "RefererHash" = 3594120000172545465 GROUP BY "URLHash", date_trunc('day', from_unixtime("EventTime")) ORDER BY PageViews DESC LIMIT 10 OFFSET 100;
