SELECT "Title", COUNT(*) AS PageViews FROM hits WHERE "CounterID" = 62 AND to_timestamp("EventDate" * 86400) >= '2013-07-01' AND to_timestamp("EventDate" * 86400) <= '2013-07-31' AND "DontCountHits" = 0 AND "IsRefresh" = 0 AND CAST("Title" AS TEXT) <> '' GROUP BY "Title" ORDER BY PageViews DESC LIMIT 10;
